import React, {useState} from 'react'
import {Card, Form, Button} from 'react-bootstrap';

export default function LoginForm() {

    const[empid,setEmpid] = useState('');
    const[password,setPassword] = useState('');
    const[keepMeLoggedIn, setKeepMeLoggedIn] = useState('true');

    const submit = (e) =>
    {
        console.log(empid);
    }
    console.log("Hello world");

    return (
       
        <Card style = {{width: "20rem", marginTop: "10px"}} bg = "dark">
                    <Card.Body>
                        <Card.Title style = {{color: "white"}}>
                            Login to your account
                        </Card.Title>
                        <Card.Body>
                            <Form>
                                <Form.Control type="text" value = {empid} placeholder="Employee ID" onChange = {(e) => setEmpid(e.target.value)}/>
                                <br/>
                                <Form.Control type="password" value = {password} placeholder="Your password" onChange = {(e) => setPassword(e.target.value)}/>
                                <br/>
                                <Form.Check type="checkbox" value = {keepMeLoggedIn} label="Keep me signed in." style = {{color: "white"}} onChange = {(e) => setKeepMeLoggedIn(!e.target.value)}/>
                                <br/>
                                <Button variant="light" type="submit" onClick = {submit}>Log In</Button>
                            </Form>
                        </Card.Body>
                    </Card.Body>
                </Card>
    )
}
