import React, { useState } from 'react'
import { Row, Col, Card, Collapse, Button } from 'react-bootstrap';
import { Timeline, Event } from 'react-timeline-scribble';



export default function StaggeredText() {

    const [open, setOpen] = useState(false);
    const [open2, setOpen2] = useState(false);
    return (
        <div>
                   <Timeline>
                       <Event interval = "Step 1" title = "Sign in/ Sign up">
                                    1. Sign in to your account
                                    <i
                                    className = "material-icons"
                                    onClick={() => setOpen(!open)}
                                    aria-controls="description"
                                    aria-expanded={open}
                                    style = {{cursor:"pointer"}}>
                                    expand_more
                                </i>
                                <Collapse in={open}>
                                    <div id="description">
                                        Sign in to your account using your <b>Virtusa employee ID and password</b>. <br/>
                                        Not a registered user yet? Sign up using your employee ID and a simple KYC process!
                                    </div>
                                </Collapse>           
                       </Event>
                   </Timeline>
        </div>
    )
}
